<template>    <div class="flex items-center py-[12px]" v-if="chatModel.modelList.length">        <el-dropdown @command="handleCommandModel">            <div                class="flex justify-center items-center rounded-lg h-[30px] px-[14px]"                style="box-shadow: 0 2px 6px 0 rgba(0, 0, 0, 0.1)"            >                <span class="text-[#415058] mr-[6px]">                    {{ chatModel.current.alias }}                </span>                <el-icon color="#415058">                    <arrow-down />                </el-icon>            </div>            <template #dropdown>                <el-dropdown-menu>                    <el-dropdown-item                        v-for="(item, index) in chatModel.modelList"                        :key="index"                        :command="item"                    >                        {{ item.alias }}                    </el-dropdown-item>                </el-dropdown-menu>            </template>        </el-dropdown>        <div            class="ml-[10px] text-[#999999]"            v-if="!chatModel?.current?.member_free"        >            消耗            <span class="text-primary">{{                chatModel?.current?.balance || 0            }}</span>            条对话次数        </div>        <div class="ml-[10px] text-[#999999]" v-else>            已开通会员，不消耗条数        </div>    </div></template><script setup lang="ts">import { ArrowDown } from '@element-plus/icons-vue'import { getChatModelApi } from '@/api/chat'const emit = defineEmits(['update:modelValue'])const props = defineProps({    modelValue: {        type: String,        default: ''    }})// 聊天模型数据const chatModel = reactive({    current: {        balance: 1,        key: '',        member_free: false,        model: '',        default: false    } as any,    modelList: [] as any[]})// 获取聊天模型数据const getChatModelFunc = async () => {    try {        const data = await getChatModelApi()        chatModel.modelList = data        chatModel.current = data.find((item: any) => item.default) || data[0]        emit('update:modelValue', chatModel.current.key)    } catch (error) {        console.log('获取聊天模型数据错误=>', error)    }}const handleCommandModel = (model: any) => {    chatModel.current = model    emit('update:modelValue', chatModel.current.key)}getChatModelFunc()</script>