<template>    <div class="l-textarea" :data-error="error" :class="{ error: error }" :style="customStyle">        <textarea            class="l-textarea__inner"            :placeholder="placeholder"            :rows="rows"            :value="textAreaValue"            :maxlength="maxlength"            @input="getTextAreaValue"        />        <div v-if="showWordLimit" class="l-textarea-length-maxlength">            <span>{{ lengthText }}</span> / <span>{{ maxlength }}</span>        </div>        <slot name="length-suffix"></slot>    </div></template><script setup>import { watch, ref, nextTick } from 'vue'const props = defineProps({    placeholder: '',    maxlength: '',    error: '',    rows: '',    customStyle: {},    showWordLimit: false,    modelValue: ''})const emit = defineEmits(['update:modelValue'])const textAreaValue = ref(props.modelValue)const lengthText = ref(props.modelValue?.length || 0)const getTextAreaValue = (e) => {    const event = e || window.event    const target = event.srcElement || event.taget    emit('update:modelValue', target.value)}watch(    () => props.modelValue,    (val) => {        textAreaValue.value = val        lengthText.value = val.length    })</script><style lang="scss" scoped>.l-textarea {    width: 100%;    position: relative;    border-radius: 6px;    box-sizing: border-box;    overflow: hidden;    border: 1px solid var(--el-color-primary);    background: rgba(255, 255, 255, 0.08);}.l-textarea-length-maxlength {    position: absolute;    bottom: 10px;    right: 10px;    font-size: 14px;    color: #909499;    background-color: #FFFFFF;}.l-textarea__inner {    width: 100%;    padding: 8px;    box-sizing: border-box;    transition: border 0.3s;    outline: none;    /** 禁止textarea拉伸 */    resize: none;}</style>