<template>    <div        v-if="drawModel.modelList.length"        class="flex items-center justify-between py-[12px]"    >        <el-dropdown @command="handleCommandModel">            <div                class="flex justify-center items-center rounded-[30px] h-[30px] px-[14px]"                style="box-shadow: 0 2px 6px 0 rgba(0, 0, 0, 0.1)"            >                <span class="text-[#415058] mr-[6px]">                    {{ drawModel.current.name }}                </span>                <el-icon color="#415058">                    <arrow-down />                </el-icon>            </div>            <template #dropdown>                <el-dropdown-menu>                    <el-dropdown-item                        v-for="(item, index) in drawModel.modelList"                        :key="index"                        :command="item"                    >                        {{ item.name }}                    </el-dropdown-item>                </el-dropdown-menu>            </template>        </el-dropdown>        <div class="ml-[10px] text-[#999999]">            消耗            <span class="text-primary">                {{ drawModel?.current?.balance || 0 }}            </span>            条绘画条数        </div>    </div></template><script setup lang="ts">import { ArrowDown } from '@element-plus/icons-vue'import { drawingModel } from '~/api/drawing'const emit = defineEmits(['update:modelValue'])const props = defineProps({    modelValue: {        type: String,        default: ''    }})// 绘画模型const drawModel = reactive({    current: {        balance: 1,        name: '',        model: ''    } as any,    modelList: [] as any[]})// 获取绘画模型数据const getDrawModel = async () => {    try {        const data = await drawingModel()        drawModel.modelList = data        drawModel.current = data.find((item: any) => item.default) || data[0]        emit('update:modelValue', drawModel.current.model)    } catch (error) {        console.log('获取绘画模型失败=>', error)    }}const handleCommandModel = (model: any) => {    drawModel.current = model    emit('update:modelValue', drawModel.current.model)}getDrawModel()</script>