<template>    <div        v-if="drawModel.modelList.length"        class="flex items-center justify-between pb-[20rpx] text-xs"    >        <!-- 模型选择 -->        <view            class="inline-flex justify-center items-center rounded-[30px] h-[60rpx] px-[24rpx]"            style="box-shadow: 0 2px 6px 0 rgba(0, 0, 0, 0.1)"            @click="drawModel.show = true"        >            <text class="text-[#415058] mr-[6px]">                {{ drawModel.current.name }}            </text>            <u-icon name="arrow-down" size="24rpx"></u-icon>        </view>        <view class="ml-[10px] text-[#999999]">            <text>消耗 </text>            <text class="text-primary">                {{ drawModel?.current?.balance || 0 }}            </text>            <text>条绘画条数</text>        </view>    </div>    <u-picker        v-model="drawModel.show"        :safe-area-inset-bottom="true"        :confirm-color="$theme.primaryColor"        mode="selector"        :range="drawModel.modelList"        range-key="name"        @confirm="handleChoiceModel"    ></u-picker></template><script setup lang="ts">import { reactive } from 'vue'import { drawingModel } from '@/api/drawing'const emit = defineEmits(['update:modelValue'])const props = defineProps({    modelValue: {        type: String,        default: ''    }})// 绘画模型const drawModel = reactive({    show: false,    current: {        balance: 1,        name: '',        model: ''    } as any,    modelList: [] as any[]})// 获取绘画模型数据const getDrawModel = async () => {    try {        const data = await drawingModel()        drawModel.modelList = data        drawModel.current = data.find((item: any) => item.default) || data[0]        emit('update:modelValue', drawModel.current.model)    } catch (error) {        console.log('获取绘画模型失败=>', error)    }}const handleChoiceModel = (event: any) => {    drawModel.current = drawModel.modelList[event[0]]    emit('update:modelValue', drawModel.current.model)}getDrawModel()</script>